generated with <a href=\"https://www.advancedcustomfields.com/\">Advanced Custom Fields</a> and uses WFV.\r\n<p style=\"text-align: center;\"><a href=\"https://github.com/macder/wfv-validation\">Code Repo</a> / <a href=\"https://macder.github.io/wfv/\">User Documentation</a></p>'),(3512,301,'_summary_body','field_5a4ab43dee001'),(3513,301,'summary_list_0_name','PHP'),(3514,301,'_summary_list_0_name','field_5a4ab477ee003'),(3515,301,'summary_list_1_name','WP-CLI'),(3516,301,'_summary_list_1_name','field_5a4ab477ee003'),(3517,301,'summary_list','4'),(3518,301,'_summary_list','field_5a4ab453ee002'),(3519,301,'summary',''),(3520,301,'_summary','field_5a4ab407edfff'),(3521,301,'media_type','gallery'),(3522,301,'_media_type','field_5acaaf2e198c9'),(3523,301,'gallery_0_image','302'),(3524,301,'_gallery_0_image','field_5a4ab3a6ce284'),(3525,301,'gallery_1_image','303'),(3526,301,'_gallery_1_image','field_5a4ab3a6ce284'),(3527,301,'gallery','3'),(3528,301,'_gallery','field_5a4ab373ce283'),(3529,301,'project_type','a:1:{i:0;s:2:\"10\";}'),(3530,301,'_project_type','field_5acc188943b6e'),(3531,304,'_wp_attached_file','2018/04/wfv_large_03.jpg'),(3532,304,'_wp_attachment_metadata','a:5:{s:5:\"width\";i:814;s:6:\"height\";i:461;s:4:\"file\";s:24:\"2018/04/wfv_large_03.jpg\";s:5:\"sizes\";a:3:{s:9:\"thumbnail\";a:4:{s:4:\"file\";s:24:\"wfv_large_03-150x150.jpg\";s:5:\"width\";i:150;s:6:\"height\";i:150;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:6:\"medium\";a:4:{s:4:\"file\";s:24:\"wfv_large_03-300x170.jpg\";s:5:\"width\";i:300;s:6:\"height\";i:170;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:12:\"medium_large\";a:4:{s:4:\"file\";s:24:\"wfv_large_03-768x435.jpg\";s:5:\"width\";i:768;s:6:\"height\";i:435;s:9:\"mime-type\";s:10:\"image/jpeg\";}}s:10:\"image_meta\";a:12:{s:8:\"aperture\";s:1:\"0\";s:6:\"credit\";s:0:\"\";s:6:\"camera\";s:0:\"\";s:7:\"caption\";s:0:\"\";s:17:\"created_timestamp\";s:1:\"0\";s:9:\"copyright\";s:0:\"\";s:12:\"focal_length\";s:1:\"0\";s:3:\"iso\";s:1:\"0\";s:1